mapper.select:
select state
from ad
where adid = #{adid}

select status
from ad_state_log
where year = #{param.year}
and month = #{param.month}
and day = #{param.day}
and  create_time <= #{param.current} 
and  create_time >= #{param.last_1_hour} 
and adid = #{param.adid}

select status
from ad_state_log
where year = #{param.year}
and month = #{param.month}
and day = #{param.day}
and  create_time <= #{param.last_1_hour} 
and  create_time >= #{param.last_2_hour} 
and adid = #{param.adid}

mapper.update:
update ad
set state = 5
where adid = #{adid}
